import { defineStore } from 'pinia'
import axios from 'axios';
import router from '../router/index'
import { Preferences } from '@capacitor/preferences';

axios.defaults.baseURL = "https://gae.co.bw/"
// const test = await authStore.getToken()
export const useAuthStore = defineStore('auth', {
    state: () => {
        return {
            authenticated: false,
            base_url: "https://gae.co.bw/",
            error: null,
            token: localStorage.getItem('token') || null,
            user: null,
            dob: "",
            gender: "",
        }
    },
    getters: {
        loggedIn: (state) => (state.user ? true : false),
        //getToken: async () => await Preferences.get({ key: 'token' }),
    },
    actions: {

         async getToken() {
          return await Preferences.get({ key: 'token' });
        },

        async setToken (token) {
          await Preferences.set({
            key: 'token',
            value: token,
          });
        },

        async setUser (user) {
          await Preferences.set({
            key: 'token',
            value: token,
          });
        },

        async login(email, password) {

          const config = {
            headers:{
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            }
          };

          await axios.post('/api/profile/login', {
            email: email,
            password: password
          }, config).then((res) => {
            
            console.log('#######################################################')
            console.log("Authentication Successful");
            console.log('#######################################################')
            this.authenticated = true;

            this.user = res.data.user;

            this.setToken(res.data.token);
            

          }).catch((error) => {
            this.error = error.response.data.message;
          });

          
 
        },
        async logout() {

            const config = {
              'Accept': 'application/json',
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            };

            await axios.post('/logout', {
              },config).then(() => {
                
                this.user = null;
                localStorage.removeItem("event");
                localStorage.removeItem("events");
                localStorage.removeItem("timer");

              }).catch(() => {
          
                this.user = null;
                localStorage.removeItem("event");
                localStorage.removeItem("events");
                localStorage.removeItem("timer");

              });

              router.push('/login')
              
        }
    }
})
