const requireAuth = async (to, from, next) => {
  console.log(useAuthStore().loggedIn)
  if (useAuthStore().loggedIn == false) {
    next({ name: 'login' })
  } else {
    next()
  }
}

const checkSession = async (to, from, next) => {
  console.log(useAuthStore().loggedIn)
  if(useAuthStore().loggedIn == true) {
    next({ name: 'home' })
  } else {
    next()
  }
}

