export const useAuthStore = defineStore('auth', {
    state: () => {
        return {
            authenticated: false,
            // base_url: "https://gae.co.bw/",
            base_url: "http://10.220.5.128:8000/",
            error: null,
            token: null,
            user: null,
            dob: "",
            gender: "",
        }
    },
    getters: {
        loggedIn: (state) => (state.user ? true : false),
        useToken:  () => Preferences.get({ key: 'token' }),
    },
    actions: {

        getToken() {
          this.token = Preferences.get({ key: 'token' });
        },

        async setToken (token) {
          await Preferences.set({
            key: 'token',
            value: token,
          });
        },

        async setUser (user) {
          await Preferences.set({
            key: 'user',
            value: JSON.stringify(user),
          });
        },

        async login(email, password) {

          const config = {
            headers:{
              'Accept': 'application/json',
              'Content-Type': 'application/json',
              'Access-Control-Allow-Origin': '*',
            }
          };

          await axios.post('/api/v1/auth/login', {
            email: email,
            device_name: 'Mobile',
            password: password
          }, config).then((res) => {
            
            console.log('#######################################################')
            console.log("Authentication Successful");
            console.log('#######################################################')
            this.authenticated = true;

            console.log(res.data)
            this.user = res.data.user;
            this.token = res.data.token;
            this.setToken(res.data.token);
            this.setUser(JSON.stringify(res.data.user));
            
          }).catch((error) => {
            this.error = error.response.data.message;
          });

          
        },

        async logout() {

            const config = {
              'Accept': 'application/json',
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            };

            await axios.post('/logout', {
              },config).then(() => {
                
                this.user = null;
                localStorage.removeItem("event");
                localStorage.removeItem("events");
                localStorage.removeItem("timer");

              }).catch(() => {
          
                this.user = null;
                localStorage.removeItem("event");
                localStorage.removeItem("events");
                localStorage.removeItem("timer");

              });

              router.push('/login')
              
        }
    }
})

import { defineStore } from 'pinia'
import {  Geolocation, GeolocationPosition, PermissionStatus  } from '@capacitor/geolocation';

export const useLocationStore = defineStore('location', {
    state: () => {
        return {
            permission: "",
            location: null,
        }
    },
    getters: {
        isPermitted: (state) => (state.location ? true : false),
    },
    actions: {
        async initLocation() {
            await this.checkLocationPermission();

            //For test ENV
            // await this.getLocation();
        },

        getLocation(){
            return new Promise((resolve, reject) => {

                if (navigator.geolocation) {
                    navigator.geolocation.getCurrentPosition(this.successCallback, this.errorCallback);
                } else {
                    this.permission = "denied"
                    console.log("Geolocation is not supported by this browser.");
                }
                
            });
        },
        // Check for location permission on mount
        async checkLocationPermission () {
            const status = await Geolocation.checkPermissions();

            this.permission = status;

            if (status.location === 'denied') {
                console.log('Location permission denied.');
            } else if (status.location === 'granted') {
                console.log('Location permission granted.');
                this.getCurrentLocation();
            } else {
                console.log('Location permission not requested. Requesting permission now.');
                this.requestLocationPermission();
            }

            this.getCurrentLocation();
        },

        // Request location permission
        async requestLocationPermission () {
            const status = await Geolocation.requestPermissions();

            this.permission = status;

            if (status.location === 'granted') {
                console.log('Location permission granted.');
                this.getCurrentLocation();
            } else {
                console.log('Location permission denied.');
            }
        },

        // Get current location
        async getCurrentLocation () {
            const pos = await Geolocation.getCurrentPosition();
            this.location = pos;
        },

        async successCallback (position) {
            const latitude = position.coords.latitude;
            const longitude = position.coords.longitude;

            const array = {"coords": {"latitude": latitude, "longitude": longitude}}
            this.location = array;
            console.log("Latitude: " + latitude + " Longitude: " + longitude);
        },

        async errorCallback(error) {
            this.permission = "denied"
            console.log("Unable to retrieve your location due to " + error.code + ": " + error.message);
        }
    }
})
